jQuery(document).ready(function(){jQuery(".bvote-up").on("click",function(a){a.preventDefault();bvote(jQuery(this).data("postid"),1)});jQuery(".bvote-down").on("click",function(a){a.preventDefault();bvote(jQuery(this).data("postid"),-1)})});
